package com.kodnest.prgms;

import java.io.Serializable;

public class Student implements Serializable{//bean class
	public Student() {
		// TODO Auto-generated constructor stub
	}
	private String name;
	private int id;
	private int age;
	public String getName() {
		return name;
	}
	public void setName(String name) {
		this.name = name;
	}
	public int getId() {
		return id;
	}
	public void setId(int id) {
		this.id = id;
	}
	public int getAge() {
		return age;
	}
	public void setAge(int age) {
		this.age = age;
	}
	
}
